mapper.select:
  	SELECT 
  		count(id)	
  	FROM 
  		resource_info r
  	  
	      
	        name like #{name}
	      
	      
	        AND status=#{status}  
	    
	      
	        AND id=#{id}

  	SELECT *
FROM (
	SELECT tt.*, ROWNUM AS rowno
    FROM (  
    SELECT 
  		
	    ,
	    	
  	FROM 
  		resource_info r
  	  
	      
	        name like #{name}
	      
	      
	        AND status=#{status}  
	      
	      
	        AND id=#{id}  
	     
		
		order by r.status, r.create_date desc
     ) tt
   WHERE ROWNUM <= ${sqlMap.end}
 ) ta  
 WHERE ta.rowno >= ${sqlMap.start}

select
,
from resource_info r where id = #{id,jdbcType=INTEGER}

