    WHERE r.subject_id=s.id
    AND r.subject_id=#{subjectId}
     ) tt
   WHERE ROWNUM <= ${end}
 ) ta  
 WHERE ta.rowno >= ${start}

    SELECT *
FROM (
	SELECT tt.*, ROWNUM AS rowno
    FROM (  
    	SELECT 
    
    ,
    
    FROM resource_info r,business_resource s
    WHERE r.id=s.resource_id
    AND s.business_id=#{businessId}
     ) tt
   WHERE ROWNUM <= ${end}
 ) ta  
 WHERE ta.rowno >= ${start}

