mapper.select:
  	SELECT 
	    count(id)
	    FROM subject_info r
	      
		      
		        name like #{name}
		     
		      
		        AND parent_id=#{parentId}  
		      
		      
		        AND share_region=#{shareRegion}  
		     
		      
		        AND share_mode=#{shareMode}  
		     
		      
		        AND status=#{status}  
		     
		      
		        AND id = #{id}

    SELECT *
	FROM (
		SELECT tt.*, ROWNUM AS rowno
	    FROM (  
	    	SELECT 
	    
	    ,
	    
	    FROM subject_info r
	      
		      
		        name like #{name}
		     
		      
		        AND parent_id=#{parentId}  
		      
		      
		        AND share_region=#{shareRegion}  
		     
		      
		        AND share_mode=#{shareMode}  
		     
		      
		        AND status=#{status}  
		     
		      
		        AND id = #{id}
		    
			
	     ) tt
	   WHERE ROWNUM <= ${sqlMap.end}
	 ) ta  
	 WHERE ta.rowno >= ${sqlMap.start}

    SELECT *
	FROM (
		SELECT tt.*, ROWNUM AS rowno
	    FROM (  
	    SELECT 
	    
	    ,
	    
	    FROM subject_info r,business s
	    WHERE r.id=s.subject_id
	    AND s.id=#{businessId}
	     ) tt
	   WHERE ROWNUM <= ${end}
	 ) ta  
	 WHERE ta.rowno >= ${start}

    SELECT *
FROM (
	SELECT tt.*, ROWNUM AS rowno
    FROM (  
    SELECT 
    
    ,
    
    FROM subject_info r,resource_info s
    WHERE r.id=s.subject_id
    AND s.id=#{resourceId}
     ) tt
   WHERE ROWNUM <= ${end}
 ) ta  
 WHERE ta.rowno >= ${start}

SELECT * FROM subject_info WHERE status=2 AND del_flag=0
AND name like CONCAT('%', #{name}, '%')
ORDER BY create_date desc ${sqlMap.page}
